import React, { useState, useEffect } from "react";
import "./App.css";

import dorgaIcon from "./dorgaIcon.png";

const App = () => {
  const [gridSize, setGridSize] = useState(3);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    resetGame();
  }, [gridSize]);

  const randomIndex = Math.floor(Math.random() * (gridSize * gridSize));

  const handleClick = (index) => {
    if (index === randomIndex) {
      setSelectedImageIndex(index);
      setGameOver(true);
    } else {
      const newImages = [...images];
      newImages[index].faded = true;
      setImages(newImages);
    }
  };

  const resetGame = () => {
    setLoading(true);
    const newImages = Array.from(
      { length: gridSize * gridSize },
      (_, index) => ({
        url: dorgaIcon,
        faded: false,
      })
    );
    setImages(newImages);
    setSelectedImageIndex(null);
    setGameOver(false);

    setTimeout(() => {
      setLoading(false);
    }, 2500); // Adjust the timeout as needed for the loading animation
  };

  return (
    <div className="App">
      <h1>Image Grid Game</h1>
      <div className="button-group">
        <button onClick={() => setGridSize(3)}>3x3 Grid</button>
        <button onClick={() => setGridSize(4)}>4x4 Grid</button>
      </div>
      {loading ? (
        <div className="loading">
          <img src={dorgaIcon} className="loadingAnimation" />
        </div>
      ) : (
        <div className={`grid grid-${gridSize}`}>
          {images.map((img, index) => (
            <div
              key={index}
              className={`grid-item ${img.faded ? "faded" : ""}`}
              onClick={() => handleClick(index)}
            >
              <img src={img.url} alt={`Grid ${index}`} />
            </div>
          ))}
        </div>
      )}
      {gameOver && (
        <div className="modal">
          <img src={dorgaIcon} alt="Selected" />
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
    </div>
  );
};

export default App;
