import React, { useState, useEffect } from "react";
import "./App.css";
import drogaIcon from "./drogaIcon.png"; // Import the image

const App = () => {
  const [gridSize, setGridSize] = useState(3);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [gameOver, setGameOver] = useState(false);
  const [images, setImages] = useState([]);
  const [fadeOutIndices, setFadeOutIndices] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to generate images array
  const generateImages = (size) => {
    return Array.from({ length: size * size }, () => drogaIcon);
  };

  // Initialize images array based on gridSize
  useEffect(() => {
    setImages(generateImages(gridSize));
    setLoading(true);
    setTimeout(() => setLoading(false), gridSize * gridSize * 100); // Simulate loading
  }, [gridSize]);

  // Generate a random index for the selected image
  const randomIndex = Math.floor(Math.random() * images.length);

  const handleClick = (index) => {
    if (index === randomIndex) {
      setSelectedImageIndex(index);
      setGameOver(true);
    } else {
      setFadeOutIndices((prev) => [...prev, index]);
    }
  };

  const resetGame = () => {
    setSelectedImageIndex(null);
    setGameOver(false);
    setFadeOutIndices([]);
    setImages(generateImages(gridSize));
    setLoading(true);
    setTimeout(() => setLoading(false), gridSize * gridSize * 100); // Simulate loading
  };

  return (
    <div className="App">
      <h1>Image Grid Game</h1>
      <div className="button-group">
        <button onClick={() => setGridSize(3)}>3x3 Grid</button>
        <button onClick={() => setGridSize(4)}>4x4 Grid</button>
      </div>
      <div className={`grid grid-${gridSize}`}>
        {!loading &&
          images.map((img, index) => (
            <div
              key={index}
              className={`grid-item ${
                fadeOutIndices.includes(index) ? "fade-out" : ""
              }`}
              onClick={() => handleClick(index)}
              style={{ animationDelay: `${index * 100}ms` }}
            >
              <img src={img} alt={`Grid ${index}`} />
            </div>
          ))}
      </div>
      {gameOver && (
        <div className="modal">
          <img src={drogaIcon} alt="Selected" />
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
    </div>
  );
};

export default App;
