// src/App.js
import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [gridSize, setGridSize] = useState(3);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [gameOver, setGameOver] = useState(false);

  // Images array (you can replace these with actual image URLs)
  const images = Array.from(
    { length: gridSize * gridSize },
    (_, index) => `https://via.placeholder.com/150?text=${index + 1}`
  );

  // Select a random image index
  const randomIndex = Math.floor(Math.random() * images.length);

  const handleClick = (index) => {
    if (index === randomIndex) {
      setSelectedImageIndex(index);
      setGameOver(true);
    } else {
      images[index] = null; // Remove the image
    }
  };

  const resetGame = () => {
    setSelectedImageIndex(null);
    setGameOver(false);
  };

  return (
    <div className="App">
      <h1>Image Grid Game</h1>
      <div className="button-group">
        <button onClick={() => setGridSize(3)}>3x3 Grid</button>
        <button onClick={() => setGridSize(4)}>4x4 Grid</button>
      </div>
      <div className={`grid grid-${gridSize}`}>
        {images.map((img, index) =>
          img ? (
            <div
              key={index}
              className="grid-item"
              onClick={() => handleClick(index)}
            >
              <img src={img} alt={`Grid ${index}`} />
            </div>
          ) : (
            <div key={index} className="grid-item" />
          )
        )}
      </div>
      {gameOver && (
        <div className="modal">
          <img
            src={`https://via.placeholder.com/600?text=${randomIndex + 1}`}
            alt="Selected"
          />
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
    </div>
  );
};

export default App;
